mapper.select:
SELECT * FROM ( SELECT a.*, (@rownum:=@rownum+1) AS rownum, DATE_FORMAT(wdate, '%Y-%m-%d') AS formatted_date FROM tbl_news a CROSS JOIN (SELECT @rownum:=0) r WHERE 1=1 AND a.part = 'photo'
AND (a.subject LIKE CONCAT('%', #{searchKeyword}, '%') OR a.keyword_tag LIKE CONCAT('%', #{searchKeyword}, '%'))
AND DATE_FORMAT(a.wdate, '%Y-%m-%d') BETWEEN #{sdate} AND #{edate}
) A ORDER BY num DESC

SELECT COUNT(*) FROM tbl_news a WHERE 1=1 AND a.part = 'photo'
AND (a.subject LIKE CONCAT('%', #{searchKeyword}, '%') OR a.keyword_tag LIKE CONCAT('%', #{searchKeyword}, '%'))
AND DATE_FORMAT(a.wdate, '%Y-%m-%d') BETWEEN #{sdate} AND #{edate}
